$(".nav-tabs-dropdown").each(function(t,e){$(e).text($(e).next("ul").find("li.active a").text())}),$(".nav-tabs-dropdown").on("click",function(t){t.preventDefault(),$(t.target).toggleClass("open").next("ul").slideToggle()}),$('#nav-tabs-wrapper a[data-toggle="tab"]').on("click",function(t){t.preventDefault(),$(t.target).closest("ul").hide().prev("a").removeClass("open").text($(this).text())});